describe OpinionsController do

  # This should return the minimal set of attributes required to create a valid
  # Opinion. As you add validations to Opinion, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all opinions as @opinions" do
      opinion = Opinion.create! valid_attributes
      get :index
      assigns(:opinions).should eq([opinion])
    end
  end

  describe "GET show" do
    it "assigns the requested opinion as @opinion" do
      opinion = Opinion.create! valid_attributes
      get :show, :id => opinion.id.to_s
      assigns(:opinion).should eq(opinion)
    end
  end

  describe "GET new" do
    it "assigns a new opinion as @opinion" do
      get :new
      assigns(:opinion).should be_a_new(Opinion)
    end
  end

  describe "GET edit" do
    it "assigns the requested opinion as @opinion" do
      opinion = Opinion.create! valid_attributes
      get :edit, :id => opinion.id.to_s
      assigns(:opinion).should eq(opinion)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Opinion" do
        expect {
          post :create, :opinion => valid_attributes
        }.to change(Opinion, :count).by(1)
      end

      it "assigns a newly created opinion as @opinion" do
        post :create, :opinion => valid_attributes
        assigns(:opinion).should be_a(Opinion)
        assigns(:opinion).should be_persisted
      end

      it "redirects to the created opinion" do
        post :create, :opinion => valid_attributes
        response.should redirect_to(Opinion.last)
