        assigns(:opinion).should be_a_new(Opinion)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Opinion.any_instance.stub(:save).and_return(false)
        post :create, :opinion => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested opinion" do
        opinion = Opinion.create! valid_attributes
        # Assuming there are no other opinions in the database, this
        # specifies that the Opinion created on the previous line
        # receives the :update_attributes message with whatever params are
        # submitted in the request.
        Opinion.any_instance.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => opinion.id, :opinion => {'these' => 'params'}
      end

      it "assigns the requested opinion as @opinion" do
        opinion = Opinion.create! valid_attributes
        put :update, :id => opinion.id, :opinion => valid_attributes
        assigns(:opinion).should eq(opinion)
